							  if(array_key_exists('child',$value))
	  
								$html.= '<li class="'.$activeCss.' menu-item-has-children menu-item"><a href="javascript:;" '.$value['target'].' class="menu-css">'.$iconWithTExt.' <i class="fas fa-chevron-'.$icon.'"></i></a>'; 
	  
